package bean;

public class Admin {

	/**
	 * 
	 */
	public Admin() {
		super();
		// TODO Auto-generated constructor stub
	}
	private int admin_id;
	private String admin_name;
	private String admin_pswd;
	private String admin_email;
	/**
	 * @return the admin_id
	 */
	public int getAdmin_id() {
		return admin_id;
	}
	/**
	 * @param admin_id the admin_id to set
	 */
	public void setAdmin_id(int admin_id) {
		this.admin_id = admin_id;
	}
	/**
	 * @return the admin_name
	 */
	public String getAdmin_name() {
		return admin_name;
	}
	/**
	 * @param admin_name the admin_name to set
	 */
	public void setAdmin_name(String admin_name) {
		this.admin_name = admin_name;
	}
	/**
	 * @return the admin_pswd
	 */
	public String getAdmin_pswd() {
		return admin_pswd;
	}
	/**
	 * @param admin_pswd the admin_pswd to set
	 */
	public void setAdmin_pswd(String admin_pswd) {
		this.admin_pswd = admin_pswd;
	}
	/**
	 * @return the admin_email
	 */
	public String getAdmin_email() {
		return admin_email;
	}
	/**
	 * @param admin_email the admin_email to set
	 */
	public void setAdmin_email(String admin_email) {
		this.admin_email = admin_email;
	}
}
